import React, { useState } from 'react';
import './ProfileForm.css';

const ProfileForm = () => {
  // State for form fields
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    age: '',
    bio: ''
  });

  // State for validation errors
  const [errors, setErrors] = useState({});

  // State for submission status
  const [isSubmitted, setIsSubmitted] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Clear error for this field when user starts typing
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    // Name validation
    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    // Email validation
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    // Age validation
    if (!formData.age) {
      newErrors.age = 'Age is required';
    } else if (isNaN(formData.age) || parseInt(formData.age) < 13) {
      newErrors.age = 'Age must be a number 13 or greater';
    }

    // Bio validation (optional but with character limit)
    if (formData.bio.length > 500) {
      newErrors.bio = 'Bio cannot exceed 500 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Form is valid, proceed with submission
      // In a real app, you'd send data to a server here
      console.log('Form submitted:', formData);
      setIsSubmitted(true);
    }
  };

  // Reset form to initial state
  const handleReset = () => {
    setFormData({
      name: '',
      email: '',
      age: '',
      bio: ''
    });
    setErrors({});
    setIsSubmitted(false);
  };

  return (
    <div className="form-container">
      <h1 className="form-title">User Profile</h1>

      {isSubmitted ? (
        <div className="success-container">
          <div className="success-message">
            Profile submitted successfully!
          </div>
          <div className="profile-summary">
            <h2 className="summary-title">Your Profile:</h2>
            <p><strong>Name:</strong> {formData.name}</p>
            <p><strong>Email:</strong> {formData.email}</p>
            <p><strong>Age:</strong> {formData.age}</p>
            <p><strong>Bio:</strong> {formData.bio || 'None provided'}</p>
          </div>
          <button 
            onClick={handleReset}
            className="submit-button"
          >
            Create New Profile
          </button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label" htmlFor="name">
              Name*
            </label>
            <input
              id="name"
              name="name"
              type="text"
              value={formData.name}
              onChange={handleChange}
              className={`form-input ${errors.name ? 'error-input' : ''}`}
            />
            {errors.name && <p className="error-message">{errors.name}</p>}
          </div>

          <div className="form-group">
            <label className="form-label" htmlFor="email">
              Email*
            </label>
            <input
              id="email"
              name="email"
              type="email"
              value={formData.email}
              onChange={handleChange}
              className={`form-input ${errors.email ? 'error-input' : ''}`}
            />
            {errors.email && <p className="error-message">{errors.email}</p>}
          </div>

          <div className="form-group">
            <label className="form-label" htmlFor="age">
              Age*
            </label>
            <input
              id="age"
              name="age"
              type="number"
              value={formData.age}
              onChange={handleChange}
              className={`form-input ${errors.age ? 'error-input' : ''}`}
            />
            {errors.age && <p className="error-message">{errors.age}</p>}
          </div>

          <div className="form-group">
            <label className="form-label" htmlFor="bio">
              Bio (optional)
            </label>
            <textarea
              id="bio"
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              rows="3"
              className={`form-input form-textarea ${errors.bio ? 'error-input' : ''}`}
            />
            <p className="character-count">
              {formData.bio.length}/500 characters
            </p>
            {errors.bio && <p className="error-message">{errors.bio}</p>}
          </div>

          <button
            type="submit"
            className="submit-button"
          >
            Submit Profile
          </button>
        </form>
      )}
    </div>
  );
};

export default ProfileForm;
